function copyFunctionPre(contentCopy) {
  const copyText = document.getElementById(contentCopy).textContent;
  const textArea = document.createElement('textarea');

  textArea.setAttribute("class", "khungcon");
  textArea.textContent = copyText;
  document.body.append(textArea);
  textArea.select();
  document.execCommand("copy");
}

